import React, { useState, useCallback } from 'react';
import { Form, Input, Button } from 'antd';
import AppLayout from '../components/AppLayout';
import useInput from '../lib/useInput';
import styled from 'styeld-components';
const signup = () => {

    const [id, onChangeId] = useInput('')
    const [nickname, onChangeNickname] = useInput('')
    const [password, onChangePassword] = useInput('')
    const [passwordCheck, setPasswordCheck] = useState('')
    const [passwordError, setPasswordError] = useState(false)
    const [term, setTerm] = useState('')


    const onChangeTerm = useCallback(() => {

    }, [])

    const onChangePasswordCheck = useCallback(e => {
        setPasswordCheck(e.traget.value);
        setPasswordError(e.target.value === password);
    }, [password])

    const onSubmit = useCallback(() => {
      console.log(id,password,passwordCheck)
    }, [id,password,passwordCheck])

  return (
    <AppLayout>
      <Form onFinish={onSubmit}>
      <div>
        <label htmlFor="user-id">아이디</label>
        <br />
        <Input name="user-id" value={id} onChange={onChangeId} required />
      </div>
      <div>
        <label htmlFor="user-nickname">닉네임</label>
        <br />
        <Input name="user-id" value={nickname} onChange={onChangeNickname} required />
      </div>
      <div>
        <label htmlFor="user-id">비밀번호</label>
        <br />
        <Input name="user-password" type="password" value={password} onChange={onChangePassword} required />
      </div>
      <div>
        <label htmlFor="user-id">비밀번호 확인</label>
        <br />
        <Input
        name="user-passwordCheck" 
        type="password"
        value={passwordCheck} 
        onChange={onChangePasswordCheck} 
        required
         />
         {passwordError && <ErrorMessage></ErrorMessage>}
      </div>
      <Button type="primary" htmlType="submit">확인</Button>
      </Form>
    </AppLayout>
  )
}

export default signup;
