import React, { useCallback } from 'react';
import { Form, Input } from 'antd';
import AppLayout from '../components/AppLayout';
import useInput from '../lib/useInput';

const signup = () => {

    const [id, onChangeId] = useInput('')
    const [password, onChangePassword] = useInput('')
    const [passwordCheck, onChangePasswordCheck] = useInput('')

    const onSubmit = useCallback(() => {
      console.log(id,password,passwordCheck)
    }, [id,password,passwordCheck])

  return (
    <AppLayout>
      <Form onFinish={onSubmit}>
      <div>
        <label htmlFor="user-id">아이디</label>
        <br />
        <Input name="user-id" value={id} onChange={onChangeId}></Input>
      </div>
      <div>
        <label htmlFor="user-id">비밀번호</label>
        <br />
        <Input name="user-id" value={password} onChange={onChangePassword}></Input>
      </div>
      <div>
        <label htmlFor="user-id">비밀번호 확인</label>
        <br />
        <Input name="user-id" value={passwordCheck} onChange={onChangePasswordCheck}></Input>
      </div>
      <button htmlType=""></button>
      </Form>
    </AppLayout>
  )
}

export default signup;
